/*
JavaScript Basics
*/

/*
Data Types
Data types are like parts of speech for a programming language
*/

// Numeric data type
let academicYear = 2023;

// String
let student = 'Sophia';

// Boolean
let graduating = false;

// Undefined
let major;

// Null
let address = null;

// Array
let groceries;
groceries = ['bread', 'eggs', 'apples', 'lettuce'];
let fruit = groceries[2];
let groceryList = groceries.length;

/*
JavaScript Operators
*/

// Assignment operator
// =

// Arithmetic operators
// +, -, /, *, ++, --, %

// String operators
// +

// Comparison operators compare values and return true or false
// ==, ===, !=, !==, >, <, >=, <=

// Logical operators combine expressions and return true or false
// &&, ||, ! 

// JS expression that uses 2 or more values to return a single value
let percentage = 44 / 48;

